/* eslint-disable react-hooks/exhaustive-deps */
import { useState } from "react";

import { Avatar, Card, Image, Table } from "antd";
import { DatabaseOutlined } from "@ant-design/icons";

import { pathPublic } from "../../constants/Backend";
import { motoTaxi } from "../../models/motoTaxi";
import { motoTaxisService } from "../../services";
import { useDataTable } from "../../hooks/useDataTable";

import { Boton } from "../../components/Boton";
import { ModalMotoTaxi as Modal } from "./motoTaxi.modal";
import { InfoMotoTaxi as Drawer } from "./motoTaxi.drawer";

import Registros from "./registros.table";

const TablaMotoTaxi = () => {
  const model = "Moto Taxi";
  const [verModalRegistros, setVerModalRegistros] = useState(false);
  const {
    agregar,
    loading,
    columns,
    data,
    paginacion,
    handleTableChange,
    verModal,
    setVerModal,
    verDetalle,
    setVerDetalle,
    datoSeleccionado,
    setDatoSeleccionado,
    tipo,
    traerDatos,
  } = useDataTable({
    model: motoTaxi,
    service: motoTaxisService,
    tabla: {
      columns: [
        {
          title: "Nombre",
          dataIndex: "nombre_moto",
          key: "nombre_moto",
        },
        {
          title: "Placa",
          dataIndex: "placa",
          key: "placa",
        },
        {
          title: "Color",
          dataIndex: "color",
          key: "color",
        },
        {
          title: "Entrega",
          dataIndex: "url_foto_entrega",
          key: "url_foto_entrega",
          render: (text, record) =>
            text ? (
              <Avatar
                src={
                  <Image
                    src={`${pathPublic}${record.url_foto_entrega}`}
                    fallback="data:image/png;base64,"
                    style={{
                      width: 32,
                    }}
                  />
                }
              />
            ) : (
              "-"
            ),
        },
        {
          title: "Recepcion",
          dataIndex: "url_foto_recepcion",
          key: "url_foto_recepcion",
          render: (text, record) =>
            text ? (
              <Avatar
                src={
                  <Image
                    src={`${pathPublic}${record.url_foto_recepcion}`}
                    fallback="data:image/png;base64,"
                    style={{
                      width: 32,
                    }}
                  />
                }
              />
            ) : (
              "-"
            ),
        },
        {
          title: "Responsable",
          dataIndex: "persona",
          key: "persona",
        },
      ],
      aditionalActions: [
        {
          title: "Registros",
          onClick: (record) => {
            registros(record);
          },
          icon: <DatabaseOutlined style={{ fontSize: 20, color: "orange" }} />,
        },
      ],
      info: true,
      edit: true,
      delete: true,
    },
  });

  const registros = (record) => {
    setDatoSeleccionado(record);
    setVerModalRegistros(true);
  };

  return (
    <Card
      title={`Lista de ${model}s`}
      extra={
        <Boton type="primary" onClick={agregar} name={`Agregar ${model}`} />
      }
    >
      <Table
        style={{ width: "100%", textAlign: "center" }}
        className="gx-table-responsive"
        loading={loading}
        columns={columns}
        dataSource={data}
        pagination={paginacion}
        onChange={handleTableChange}
      />
      {verModal ? (
        <Modal
          traerDatos={traerDatos}
          verModal={verModal}
          datoSeleccionado={datoSeleccionado}
          setVerModal={setVerModal}
          tipo={tipo}
        />
      ) : null}
      {verDetalle ? (
        <Drawer
          show={verDetalle}
          setShow={setVerDetalle}
          data={datoSeleccionado}
        />
      ) : null}
      {verModalRegistros ? (
        <Registros
          verModal={verModalRegistros}
          datoSeleccionado={datoSeleccionado}
          setVerModal={setVerModalRegistros}
        />
      ) : null}
    </Card>
  );
};

export default TablaMotoTaxi;
